    padding: 2rem 0;
    font-family: $font-base;
    font-size: $font-larger-medium;
  }

  &__graph {
    width: 60%;
    margin-bottom: 2rem;
  }
}

@media (min-width: $mobile-landscape) {
  .item-performance {
    padding: 2rem 5rem;
