    &__graph {
      width: 80%;
    }
  }
}

@media (min-width: $tablet) {
  .item-performance {
    &__header {
      text-align: center;
      font-size: $font-big;
    }

    &__graph {
      width: 600px;
      align-self: center;
    }
  }
}

